:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"Weblog Tools Collection: WordPress Plugin Releases for 1/14";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10935";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/DaMLQnw58KE/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1850:"<h3>New plugins</h3>\n<p><a href="http://wordpress.org/extend/plugins/event-organiser/"><strong>Event Organiser</strong></a> allows you to create and maintain events, including complex reoccurring patterns, venue management (with Google maps), calendars and customisable event lists.</p>\n<p><a href="http://wordpress.org/extend/plugins/shiny-buttons/"><strong>Shiny Buttons</strong></a> will help you to generate CSS3 Button from you WordPress admin simply using a GUI.</p>\n<p><a href="http://tentblogger.com/adsense-plugin/"><strong>Simple Google Adsense</strong></a> lets you simply add Google Adsense to your blog.</p>\n<p><a href="http://wordpress.org/extend/plugins/wpmarketplace/"><strong>WP Marketplace</strong></a> is packed with all necessary features you must need to build a e-shop for your single product or a full featured complete market place.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://akismet.com/"><strong>Akismet</strong></a> filters out your comment and track-back spam for you, so you can focus on more important things.</p>\n<p><a href="http://www.foxy-press.com/"><strong>FoxyPress</strong></a> is a custom plugin made to integrate FoxyCart e-commerce functionality into your WordPress website.</p>\n<p><a href="http://wordpress.org/extend/plugins/joemobi/"><strong>Joemobi</strong></a> allows you to create native Android and BlackBerry applications from your WordPress site.</p>\n<p><a href="http://polldaddy.com/"><strong>PollDaddy</strong></a> allows you to create stunning surveys, polls, and quizzes in minutes.</p>\n<p><a href="http://wordpress.org/extend/plugins/trafficanalyzer/"><strong>Traffic Analyzer</strong></a> can be used to determine the number of visits to each post or page available in the site.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/DaMLQnw58KE" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 14 Jan 2012 13:00:48 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"James";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:3;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"WordPress.tv: MJ Tam – Building Blocks to a Successful Blog";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:27:"http://wordpress.tv/?p=8779";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:75:"http://wordpress.tv/2012/01/13/mj-tam-building-blocks-to-a-successful-blog/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1849:"<div id="v-Vc1YJf8R-1" class="video-player">\n</div>\n<br />  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/wptv.wordpress.com/8779